RUPO"] ==null?"":$row["COD_GRUPO"];		$detalles_mysql->LOGIN=$row["LOGIN"] ==null?"":$row["LOGIN"];		$detalles_mysql->ORDENES=$row["ORDENES"] ==null?"":$row["ORDENES"];		$detalles_mysql->NOTA=$row["NOTA"] ==null?"":$row["NOTA"];		$detalles_mysql->BARCODE=$row["BARCODE"] ==null?"":$row["BARCODE"];		$detalles_mysql->INDICE=$row["INDICE"] ==null?"":$row["INDICE"];		$detalles_mysql->DECONSUMO=$row["DECONSUMO"] ==null?"":$row["DECONSUMO"];		$detalles_mysql->ID=$row["ID"] ==null?"":$row["ID"];		$lista[]=$detalles_mysql;};	return $lista;}	else{
            return null;
        }}function todo(){
