<?php/** * MENUGRUPO_MYSQL.class.php * MENUGRUPO **/class MENUGRUPO_MYSQL {	public static $DATABASE_NAME = 'posgourmet';	public static $TABLE_NAME = 'menugrupo_mysql';public $CONN;	public $ID ;	public $COD_GRUPO ;	/**	 * @var NOM_GRUPO	 */	public $NOM_GRUPO ;	/**	 * @var GRUPO	 */	public $GRUPO ;	/**	 * @var ESTADO	 */	public $ESTADO ;	/**	 * @var COLORES	 */	public $COLORES ;	/**	 * @var COLORLETRA	 */	public $COLORLETRA ;	/**	 * @var IMAGEN	 */	public $IMAGEN ;	/**	 * @var ORDEN	 */	public $ORDEN ;	/**	 * @var IMGNORMAL	 */	public $IMGNORMAL ;	/**	 * @var IMGPRESION	 */	public $IMGPRESION ;	/**	 * @var IMGSOBRE	 */	public $IMGSOBRE ;	/**	 * @var IMGNODISPO	 */	public $IMGNODISPO ;	/**	 * @var IMG	 */	public $IMG ;	/**	 * @var INVISIBLE	 */	public $INVISIBLE ;	/**	 * @var DIA	 */	public $DIA ;	public $IMGNORBLOB ;	public $IMGPREBLOB ;	public $IMGSOBBLOB ;	public $IMGPROBLOB ;function MENUGRUPO_MYSQL($con) {$this->CON=$con;}	function contructor($ID,$COD_GRUPO,$NOM_GRUPO,$GRUPO,$ESTADO,$COLORES,$COLORLETRA,$IMAGEN,$ORDEN,$IMGNORMAL,$IMGPRESION,$IMGSOBRE,$IMGNODISPO,$IMG,$INVISIBLE,$DIA,$IMGNORBLOB,$IMGPREBLOB,$IMGSOBBLOB,$IMGPROBLOB){	$this->ID=$ID;	$this->COD_GRUPO=$COD_GRUPO;	$this->NOM_GRUPO=$NOM_GRUPO;	$this->GRUPO=$GRUPO;	$this->ESTADO=$ESTADO;	$this->COLORES=$COLORES;	$this->COLORLETRA=$COLORLETRA;	$this->IMAGEN=$IMAGEN;	$this->ORDEN=$ORDEN;	$this->IMGNORMAL=$IMGNORMAL;	$this->IMGPRESION=$IMGPRESION;	$this->IMGSOBRE=$IMGSOBRE;	$this->IMGNODISPO=$IMGNODISPO;	$this->IMG=$IMG;	$this->INVISIBLE=$INVISIBLE;	$this->DIA=$DIA;	$this->IMGNORBLOB=$IMGNORBLOB;	$this->IMGPREBLOB=$IMGPREBLOB;	$this->IMGSOBBLOB=$IMGSOBBLOB;	$this->IMGPROBLOB=$IMGPROBLOB;	}function insertar() { $consulta="INSERT INTO(ID,COD_GRUPO,NOM_GRUPO,GRUPO,ESTADO,COLORES,COLORLETRA,IMAGEN,ORDEN,IMGNORMAL,IMGPRESION,IMGSOBRE,IMGNODISPO,IMG,INVISIBLE,DIA,IMGNORBLOB,IMGPREBLOB,IMGSOBBLOB,IMGPROBLOB)values('".$this->ID."','".$this->COD_GRUPO."','".$this->NOM_GRUPO."','".$this->GRUPO."','".$this->ESTADO."','".$this->COLORES."','".$this->COLORLETRA."','".$this->IMAGEN."','".$this->ORDEN."','".$this->IMGNORMAL."','".$this->IMGPRESION."','".$this->IMGSOBRE."','".$this->IMGNODISPO."','".$this->IMG."','".$this->INVISIBLE."','".$this->DIA."','".$this->IMGNORBLOB."','".$this->IMGPREBLOB."','".$this->IMGSOBBLOB."','".$this->IMGPROBLOB."')"; return $this->CON->manipular($consulta);}function rellenar($resultado){        if ($resultado->num_rows > 0) {            $lista=array();            while($row = $resultado->fetch_assoc()) {	$menugrupo_mysql= new MENUGRUPO_MYSQL("");		$menugrupo_mysql->ID=$row["ID"] ==null?"":$row["ID"];		$menugrupo_mysql->COD_GRUPO=$row["COD_GRUPO"] ==null?"":$row["COD_GRUPO"];		$menugrupo_mysql->NOM_GRUPO=$row["NOM_GRUPO"] ==null?"":$row["NOM_GRUPO"];		$menugrupo_mysql->GRUPO=$row["GRUPO"] ==null?"":$row["GRUPO"];		$menugrupo_mysql->ESTADO=$row["ESTADO"] ==null?"":$row["ESTADO"];		$menugrupo_mysql->COLORES=$row["COLORES"] ==null?"":$row["COLORES"];		$menugrupo_mysql->COLORLETRA=$row["COLORLETRA"] ==null?"":$row["COLORLETRA"];		$menugrupo_mysql->IMAGEN=$row["IMAGEN"] ==null?"":$row["IMAGEN"];		$menugrupo_mysql->ORDEN=$row["ORDEN"] ==null?"":$row["ORDEN"];		$menugrupo_mysql->IMGNORMAL=$row["IMGNORMAL"] ==null?"":$row["IMGNORMAL"];		$menugrupo_mysql->IMGPRESION=$row["IMGPRESION"] ==null?"":$row["IMGPRESION"];		$menugrupo_mysql->IMGSOBRE=$row["IMGSOBRE"] ==null?"":$row["IMGSOBRE"];		$menugrupo_mysql->IMGNODISPO=$row["IMGNODISPO"] ==null?"":$row["IMGNODISPO"];		$menugrupo_mysql->IMG=$row["IMG"] ==null?"":$row["IMG"];		$menugrupo_mysql->INVISIBLE=$row["INVISIBLE"] ==null?"":$row["INVISIBLE"];		$menugrupo_mysql->DIA=$row["DIA"] ==null?"":$row["DIA"];		$menugrupo_mysql->IMGNORBLOB=$row["IMGNORBLOB"] ==null?"":$row["IMGNORBLOB"];		$menugrupo_mysql->IMGPREBLOB=$row["IMGPREBLOB"] ==null?"":$row["IMGPREBLOB"];		$menugrupo_mysql->IMGSOBBLOB=$row["IMGSOBBLOB"] ==null?"":$row["IMGSOBBLOB"];		$menugrupo_mysql->IMGPROBLOB=$row["IMGPROBLOB"] ==null?"":$row["IMGPROBLOB"];		$lista[]=$menugrupo_mysql;};	return $lista;}	else{            return null;        }}function todo(){        $consulta="select * from menugrupo_mysql ORDER BY menugrupo_mysql.COD_GRUPO";        $result=$this->CON->consulta($consulta);        return $this->rellenar($result);    }function buscarXID($id){        $consulta="select * from menugrupo_mysql where ID=".$id;        $result=$this->CON->consulta($consulta);        $menugrupo_mysql=$this->rellenar($result);        if($menugrupo_mysql==null){            return null;        }        return $this->rellenar($result);    }}